import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

const faqs = [
  {
    question: "Is this a family-friendly event?",
    answer:
      "Absolutely! We welcome grill-lovers of all ages. Thereâ€™s even a mini BBQ hero play zone for the kiddos!",
  },
  {
    question: "Do I need to bring anything?",
    answer:
      "Just your appetite and your BBQ spirit! We provide the rest â€” food, drinks, music, and fun.",
  },
  {
    question: "Are vegetarian/vegan options available?",
    answer:
      "Yes! Weâ€™ve got grilled veggie skewers, Beyond Burgers, and smoked tofu. Even plant-based fans can join the BBQverse.",
  },
  {
    question: "Where can I park?",
    answer:
      "Weâ€™ve got plenty of on-site parking and superhero valet support. Capes optional.",
  },
];

const FAQ = () => {
  const [openIndex, setOpenIndex] = useState(null);

  const toggleFAQ = (index) => {
    setOpenIndex(openIndex === index ? null : index);
  };

  return (
    <section
      id="faq"
      className="relative bg-black text-white py-20 px-6 md:px-12 z-10 font-body overflow-hidden"
    >
