  animation: fastFlameGlow 2s ease-in-out infinite;
  will-change: box-shadow;
  transform: translateZ(0);
}

.head-chef-glow {
  animation: fastHeadChefGlow 1.7s ease-in-out infinite;
  will-change: box-shadow;
  transform: translateZ(0);
}

.hover\:head-chef-glow-hover:hover {
  animation: fastHeadChefGlow 1.5s ease-in-out infinite;;
